import React, { useState} from 'react';

import './App.css';

const api = {
    key: "8ab7982656d9c2c93296f02ac3f36a0f",
    base: "url_base: 'https://api.openweathermap.org/data/2.5/",   
  }


  function App() {
    const [query, setQuery] = useState('');
    const [weather, setWeather] = useState({});

    const search = evt => {
      if (evt.key === "Enter") {
        fetch(`${api.base}weather?q=${query}&units=metric&APPID=${api.key}`)
          .then(res => res.json())
          .then(result => {
            setWeather(result);
            //setQuery('');
          });
      }
    }




const dateBuilder = () => {
  let d = new Date()
  let months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
  let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
  let day = days[d.getDay()];
  let date = d.getDate();
  let month = months[d.getMonth()];
  let year = d.getFullYear();
  return `${day} ${date} ${month} ${year}`;
}


  return (

    <div id="app">
<main>
<div className="search-box">
  <input type="text" 
  name="search" 
  className="search-bar" 
  placeholder="Enter a place..."
  onChange={e => setQuery(e.target.value)}
  value={query}
  onKeyPress={search}
   />
</div>
<div className="weather-holder">
  <div className="location">-</div>
  <div className="date"> {dateBuilder()}</div>
  <div className="temperature">-°C</div>
 <div className="temperature-sub">
   Min.-°C 
   | 
   Max.-°C
 </div>
<div className="conditions">-</div>
</div>
</main>
</div>



  );
}

export default App;
